mapper.select:
select

from rule where deleted = 0

    and id = #{id}


    and name = #{name,jdbcType=VARCHAR}


    and object_type = #{objectType,jdbcType=TINYINT}


    and label = #{label,jdbcType=VARCHAR}


    and attr = #{attr,jdbcType=VARCHAR}


    limit #{startRow},#{pageSize}

select
COUNT(*)
from rule where deleted = 0

    and id = #{id}


    and name = #{name,jdbcType=VARCHAR}


    and object_type = #{objectType,jdbcType=TINYINT}


    and label = #{label,jdbcType=VARCHAR}


    and attr = #{attr,jdbcType=VARCHAR}
